typedef struct stackNode {
    char data;
    struct stackNode *next;
} stackNode, *Stack;

void Push(Stack *S, char data) {
    stackNode *p = (stackNode*)malloc(sizeof(stackNode));
    p->data = data;
    p->next = *S;
    *S = p;
}
char GetHead(Stack S) {return S? S->data: 0;}
char Pop(Stack *S) {
    char data;
    stackNode *p = *S;
    data = p->data;
    *S = (*S)->next;
    free(p);
    return data;
}
bool isValid(char* s) {
    Stack S = NULL;
    for(char *p = s, c; *p != 0; p++) {
        if(S) {
            c = GetHead(S);
            if(c == '(' && *p == ')') Pop(&S);
            else if(c == '[' && *p == ']') Pop(&S);
            else if(c == '{' && *p == '}') Pop(&S);
            else Push(&S, *p);
        } else Push(&S, *p);
    }
    if(!S) return true;
    else return false;
}
